CREATE FUNCTION GuidToInt(@Guid UNIQUEIDENTIFIER) 
RETURNS INT
AS BEGIN
DECLARE @result INT
SELECT @result = CONVERT(INT, Convert(varbinary, SUBSTRING(REPLACE(@Guid, '-', ''), 0, 8)), 1);
RETURN @result
END
GO

CREATE FUNCTION FakePhoneNumber(@Guid UNIQUEIDENTIFIER)
RETURNS VARCHAR(64)
AS BEGIN
	DECLARE @NumberId int;
	DECLARE @result varchar(64);
	SELECT @NumberId = dbo.GuidToInt(@Guid)
	SELECT @result = '+' + SUBSTRING(CAST(@NumberId as varchar), 1, 2) + ' ' 
	+ SUBSTRING(CAST(@NumberId as varchar), 3, 4) + ' ' + SUBSTRING(CAST(@NumberId as varchar), 5, 10)
	return @result
end
GO

CREATE FUNCTION FakePostalCode(@Guid UNIQUEIDENTIFIER)
RETURNS VARCHAR(8)
AS BEGIN
	DECLARE @NumberId int;
	SELECT @NumberId = dbo.GuidToInt(@Guid)
	DECLARE @result varchar(8);
	select @result = REVERSE(CAST(@NumberID as varchar))
	return @result
end
GO

