CREATE FUNCTION FakeEmailAddress(@PersonId uniqueidentifier, @CompanyId uniqueidentifier)
returns nvarchar(128)
as begin
declare @FirstName nvarchar(64)
declare @LastName nvarchar(64)
declare @CompanyName nvarchar(64)
select @FirstName = dbo.FakeFirstName(@PersonId)
select @LastName = dbo.FakeLastName(@PersonId)
select @CompanyName = dbo.FakeCompanyName(@CompanyId)

declare @Email nvarchar(128)
select @email = 
	CAST(REPLACE(LOWER(@FirstName) + '.' + LOWER(@LastName) + '@' + LOWER(
		REPLACE(@CompanyName, ' & ', ''))
		+ '.test', ' ', '-')
		AS varchar(128)) COLLATE SQL_Latin1_General_CP1253_CI_AI
return @email
end
